package method;

public class Ex3 {

/*
 * 1부터 10까지 출력하는 함수 만들고 호출
 * */
	public static void printTen() {
	
	for (int i=1; i<=10; i++) {
		System.out.println(i);
	}
}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		리턴값, 입력값 없음
		printTen();
		printTen();
		printTen();
}

}