package method;

public class quiz1 {

	public static void cal(int no1, int no2) {
		
		System.out.println(no1 + " + " + no2 + " = " + (no1 + no2));
		System.out.println(no1 + " - " + no2 + " = " + (no1 - no2));
		System.out.println(no1 + " * " + no2 + " = " + (no1 * no2));
		System.out.println(no1 + " / " + no2 + " = " + (no1 / no2));
		System.out.println(no1 + " % " + no2 + " = " + (no1 % no2));
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	// 함수 컨트롤+클릭하면 해당 함수로 이동
		cal(10, 5);
	}

}
